import React, { useState, useEffect } from 'react'
import axios from 'axios'


function PastStats() {
    const [season, setSeason] = useState(null);
    const [seasonType, setSeasonType] = useState(null);
    const [stat, setStat] = useState(null);
    const [topPlayers, setTopPlayers] = useState([])

    const seasons = ['2014-15', '2015-16', '2016-17', '2017-18', '2018-19', '2019-20', '2020-21', '2021-22', '2022-23', '2023-24'];
    const seasonTypes = [
        { value: 'Regular%20Season', label: 'Regular Season' },
        { value: 'Playoffs', label: 'Playoffs' }
    ];
    const stats = ['Pts', 'Ast', 'Reb', 'Stl', 'Blk', 'Fg3Pct', 'FgPct', 'FtPct', 'Tov']; // Define available stats

    useEffect( () => {
            if (season && seasonType && stat) {
                axios.get(`/api/historic/top${stat}?season=${season}&seasonType=${seasonType}`)
                    .then(response => {setTopPlayers(response.data)})
                    .catch(() => {console.log("Make sure each parameter is set")})
            }
        },
        [season, seasonType, stat]
    );

    return (
        <div>
            <label>
                Select Season:
                <select value={season} onChange={e => setSeason(e.target.value)}>
                    <option value="">Select a season</option>
                    {seasons.map(season => (
                        <option key={season} value={season}>
                            {season}
                        </option>
                    ))}
                </select>
            </label>

            <label>
                Select Season Type:
                <select value={seasonType} onChange={e => setSeasonType(e.target.value)}>
                    <option value="">Select a season type</option>
                    {seasonTypes.map(type => (
                        <option key={type.value} value={type.value}>
                            {type.label}
                        </option>
                    ))}
                </select>
            </label>

            <label>
                Select Stat:
                <select value={stat} onChange={e => setStat(e.target.value)}>
                    <option value="">Select a stat</option>
                    {stats.map(stat => (
                        <option key={stat} value={stat}>
                            {stat}
                        </option>
                    ))}
                </select>
            </label>

            <div>
                <h2>Top 5 Players for {stat} in the {season} ({seasonType.replace('%20', ' ')}):</h2>
                {topPlayers.map()}
                <p>{stat}: {topPlayer[stat.toLowerCase()]}</p>
            </div>
        </div>

    )

}
