import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TopScorer = ({ season }) => {
    const [topScorer, setTopScorer] = useState(null);

    useEffect(() => {
        if (season) {
            axios.get(`/api/player-stats/top-scorer?season=${season}`)
                .then(response => {
                    setTopScorer(response.data);
                })
                .catch(error => {
                    console.error("There was an error fetching the top scorer!", error);
                });
        }
    }, [season]);

    return (
        <div>
            <h2>Top Scorer for {season}</h2>
            {topScorer ? (
                <div>
                    <p>Player: {topScorer.player}</p>
                    <p>Team: {topScorer.team}</p>
                    <p>Points: {topScorer.pts}</p>
                    {/* Add more fields as needed */}
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default TopScorer;
