import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/LatestNews.css';

function LatestNews() {
    const [articles, setArticles] = useState([]);
    const [source, setSource] = useState('');
    const [player, setPlayer] = useState('');
    const [team, setTeam] = useState('');
    const [limit, setLimit] = useState('');

    const sources = [];

    useEffect(() => {
        let url = `/api/articles`;
        let params = [];

        if (source) params.push(`source=${source}`);
        if (player) params.push(`player=${player}`);
        if (team) params.push(`team=${team}`);
        if (limit) params.push(`limit=${limit}`);

        if (params.length) {
            url += `?${params.join('&')}`;
        }

        axios.get(url)
            .then(response => {
                setArticles(response.data);
            })
            .catch(error => {
                console.log("Error fetching articles:", error);
            });
    }, [source, player, team, limit]);


    const handleSubmit = (e) => {
        e.preventDefault();
        const form = e.target;
        setSource(form.source.value);
        setPlayer(form.player.value);
        setTeam(form.team.value);
        setLimit(form.limit.value);
    };

    return (
        <div className="LatestArticles">
            <form onSubmit={handleSubmit}>
                <label>
                    Source:
                    <select value={source} onChange={e => setSource(e.target.value)}>
                        <option value="">Select a season type</option>
                        {sources.map(type => (
                            <option key={type.value} value={type.value}>
                                {type.label}
                            </option>
                        ))}
                    </select>
                </label>
                <label>
                    Player:
                    <input type="text" name="player"/>
                </label>
                <label>
                    Team:
                    <input type="text" name="team"/>
                </label>
                <label>
                    Limit:
                    <input type="number" name="limit" />
                </label>
                <button type="submit">Fetch Articles</button>
            </form>

            <div className="articles">
                {articles.map((article, index) => (
                    <div key={index} className="article">
                        <h3>{article.title}</h3>
                        <p>{article.description}</p>
                        <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default LatestNews;
